/*
  PATCH /api/meals/remove/:id(mealObjId)
*/
       const remove = async ctx => {
  const { id } = ctx.params;
  if (!ObjectId.isValid(id)) {
    ctx.status = 400; // Bad Request
    return;
  }

  try {
    const { user } = ctx.state;
    const userModel = await User.findById(user._id);
    userModel.meals.pull(id);
    userModel.save();
    ctx.body = userModel;
  } catch (e) {
    ctx.throw(500, e);
  }
};